"use strict";var express=require("express"),pug=require("pug"),cors=require("cors"),app=express();app.use(cors());var port=process.env.PORT||"8855";app.use(express.static("project")),app.get("/",function(e,p){p.sendFile(__dirname+"/project/landing.html")}),app.get("/dashboard",function(e,p){p.sendFile(__dirname+"/project/index.html")}),app.get("/login",function(e,p){p.send(pug.renderFile("project/login.pug"))}),app.get("/signup",function(e,p){p.send(pug.renderFile("project/signup.pug"))}),app.get("/profile",function(e,p){p.send(pug.renderFile("project/profile.pug"))}),app.get("/support",function(e,p){p.send(pug.renderFile("project/support.pug"))}),app.get("/success",function(e,p){p.send(pug.renderFile("project/success.pug"))}),app.get("/new-transaction",function(e,p){p.send(pug.renderFile("project/new-trans.pug"))}),app.get("*",function(e,p){p.send(pug.renderFile("project/landing.pug"))}),app.listen(port,function(){console.log("Server running on port ".concat(port))});